// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "../interfaces/IEventManager.sol";
import "../interfaces/IRefundEscrow.sol";

contract EventManager is IEventManager, Ownable, ReentrancyGuard, Pausable {
    uint256 private _eventIds;
    uint256 private _ticketIds;
    IRefundEscrow public refundEscrow;

    mapping(uint256 => Event) private _events;
    mapping(uint256 => mapping(uint256 => Zone)) private _eventZones;
    mapping(uint256 => mapping(address => bool)) private _hasTicket;
    mapping(uint256 => uint256) private _eventRevenue;

    uint256 public constant PLATFORM_FEE_PERCENTAGE = 5;
    uint256 public constant CANCELLATION_WINDOW = 14 days;
    uint256 public constant MIN_EVENT_DELAY = 1 days;

    event RevenueWithdrawn(uint256 indexed eventId, address indexed organizer, uint256 amount);
    event RefundEscrowUpdated(address indexed newEscrow);

    constructor(address refundEscrowAddress) Ownable(msg.sender) {
        require(refundEscrowAddress != address(0), "Invalid escrow address");
        refundEscrow = IRefundEscrow(refundEscrowAddress);
    }

    function setRefundEscrow(address newEscrow) external onlyOwner {
        require(newEscrow != address(0), "Invalid escrow address");
        refundEscrow = IRefundEscrow(newEscrow);
        emit RefundEscrowUpdated(newEscrow);
    }

    function createEvent(
        string memory name,
        uint256 date,
        uint256 basePrice,
        uint256[] memory zoneCapacities,
        uint256[] memory zonePrices
    ) external override whenNotPaused returns (uint256) {
        require(date > block.timestamp + MIN_EVENT_DELAY, "Invalid date");
        require(zoneCapacities.length == zonePrices.length, "Array length mismatch");
        
        _eventIds++;
        uint256 newEventId = _eventIds;
        
        _events[newEventId] = Event({
            name: name,
            date: date,
            basePrice: basePrice,
            organizer: msg.sender,
            cancelled: false,
            zoneCapacities: zoneCapacities,
            zonePrices: zonePrices
        });

        for (uint256 i = 0; i < zoneCapacities.length; i++) {
            require(zonePrices[i] >= basePrice, "Zone price below base");
            _eventZones[newEventId][i] = Zone({
                capacity: zoneCapacities[i],
                price: zonePrices[i],
                availableSeats: zoneCapacities[i]
            });
        }

        emit EventCreated(newEventId, name, date, msg.sender);
        return newEventId;
    }

    function cancelEvent(uint256 eventId) external override whenNotPaused {
        Event storage event_ = _events[eventId];
        require(event_.organizer == msg.sender || msg.sender == owner(), "Unauthorized");
        require(!event_.cancelled, "Already cancelled");
        require(block.timestamp <= event_.date, "Event already occurred");
        
        event_.cancelled = true;
        emit EventCancelled(eventId);
        
        // Notify RefundEscrow
        refundEscrow.cancelEvent(eventId);
    }

    function purchaseTicket(uint256 eventId, uint256 zoneId) 
        external 
        payable 
        override 
        nonReentrant 
        whenNotPaused 
    {
        Event storage event_ = _events[eventId];
        require(!event_.cancelled, "Event cancelled");
        require(block.timestamp < event_.date, "Event passed");
        require(!_hasTicket[eventId][msg.sender], "Already has ticket");
        
        Zone storage zone = _eventZones[eventId][zoneId];
        require(zone.availableSeats > 0, "Zone sold out");
        require(msg.value >= zone.price, "Insufficient payment");
        
        uint256 ticketId = ++_ticketIds;
        zone.availableSeats--;
        _hasTicket[eventId][msg.sender] = true;
        
        uint256 platformFee = (msg.value * PLATFORM_FEE_PERCENTAGE) / 100;
        uint256 organizerPayment = msg.value - platformFee;
        
        _eventRevenue[eventId] += organizerPayment;

        // Refund excess payment
        if (msg.value > zone.price) {
            uint256 excess = msg.value - zone.price;
            (bool refundSuccess, ) = payable(msg.sender).call{value: excess}("");
            require(refundSuccess, "Refund failed");
        }

        // Deposit payment to escrow
        refundEscrow.depositPayment{value: zone.price}(eventId, ticketId);
        
        emit TicketPurchased(eventId, ticketId, msg.sender);
    }

    function withdrawEventRevenue(uint256 eventId) external nonReentrant {
        Event storage event_ = _events[eventId];
        require(event_.organizer == msg.sender, "Not organizer");
        require(block.timestamp > event_.date, "Event not ended");
        
        uint256 amount = _eventRevenue[eventId];
        require(amount > 0, "No revenue");
        
        _eventRevenue[eventId] = 0;
        
        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Transfer failed");
        
        emit RevenueWithdrawn(eventId, msg.sender, amount);
    }

    function getEvent(uint256 eventId) external view override returns (Event memory) {
        return _events[eventId];
    }

    function getZone(uint256 eventId, uint256 zoneId) external view override returns (Zone memory) {
        return _eventZones[eventId][zoneId];
    }

    function hasEventConcluded(uint256 eventId) external view override returns (bool) {
        return block.timestamp > _events[eventId].date;
    }

    function getOrganizer(uint256 eventId) external view override returns (address) {
        return _events[eventId].organizer;
    }

    function getEventRevenue(uint256 eventId) external view returns (uint256) {
        return _eventRevenue[eventId];
    }

    function hasTicket(uint256 eventId, address user) external view returns (bool) {
        return _hasTicket[eventId][user];
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    receive() external payable {}
}

contracts/core/RefundEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "../interfaces/IRefundEscrow.sol";
import "../interfaces/IEventManager.sol";

contract RefundEscrow is IRefundEscrow, Ownable, ReentrancyGuard, Pausable {
    event TicketCancelled(uint256 indexed eventId, uint256 indexed ticketId, uint256 refundAmount);
    event EventCancelled(uint256 indexed eventId);
    event EventCancellationRefunded(uint256 indexed eventId, uint256 indexed ticketId, uint256 refundAmount);
    address private immutable _eventManager;
    mapping(uint256 => mapping(uint256 => Payment)) private payments;
    mapping(uint256 => bool) private eventCancelled;
    
    struct Payment {
        address payer;
        uint256 amount;
        PaymentStatus status;
        bool waitlistRefundEnabled;
        uint256 refundDeadline;
        bool isCancelled;
    }

    uint256 public constant REFUND_WINDOW = 14 days;
    uint256 public constant CANCELLATION_FEE_PERCENT = 5;

    modifier onlyEventManager() {
        require(msg.sender == _eventManager, "Only EventManager can call");
        _;
    }

    constructor(address eventManagerAddress) Ownable(msg.sender) {
        require(eventManagerAddress != address(0), "Invalid EventManager address");
        _eventManager = eventManagerAddress;
    }

    function depositPayment(uint256 eventId, uint256 ticketId) 
        external 
        payable 
        override 
        nonReentrant 
        whenNotPaused 
    {
        require(msg.value > 0, "Payment required");
        require(payments[eventId][ticketId].payer == address(0), "Payment exists");
        require(!eventCancelled[eventId], "Event cancelled");

        payments[eventId][ticketId] = Payment({
            payer: msg.sender,
            amount: msg.value,
            status: PaymentStatus.Pending,
            waitlistRefundEnabled: false,
            refundDeadline: block.timestamp + REFUND_WINDOW,
            isCancelled: false
        });

        emit PaymentDeposited(eventId, msg.sender, msg.value);
    }

    function cancelTicket(uint256 eventId, uint256 ticketId) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        Payment storage payment = payments[eventId][ticketId];
        require(payment.payer == msg.sender, "Not ticket owner");
        require(!payment.isCancelled, "Already cancelled");
        require(block.timestamp <= payment.refundDeadline, "Refund window closed");

        payment.isCancelled = true;
        uint256 refundAmount = payment.amount;
        
        if (block.timestamp > payment.refundDeadline - 7 days) {
            uint256 cancellationFee = (payment.amount * CANCELLATION_FEE_PERCENT) / 100;
            refundAmount = payment.amount - cancellationFee;
            payable(owner()).transfer(cancellationFee);
        }

        (bool success, ) = payable(msg.sender).call{value: refundAmount}("");
        require(success, "Refund transfer failed");
        
        emit TicketCancelled(eventId, ticketId, refundAmount);
    }

    function cancelEvent(uint256 eventId) 
        external 
        onlyEventManager 
        whenNotPaused 
    {
        eventCancelled[eventId] = true;
        emit EventCancelled(eventId);
    }

    function processEventCancellationRefund(uint256 eventId, uint256 ticketId) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        require(eventCancelled[eventId], "Event not cancelled");
        Payment storage payment = payments[eventId][ticketId];
        require(payment.payer == msg.sender, "Not ticket owner");
        require(!payment.isCancelled, "Already refunded");

        payment.isCancelled = true;
        uint256 refundAmount = payment.amount;
        
        (bool success, ) = payable(msg.sender).call{value: refundAmount}("");
        require(success, "Refund transfer failed");
        
        emit EventCancellationRefunded(eventId, ticketId, refundAmount);
    }

    function releasePayment(uint256 eventId, uint256 ticketId) 
        external 
        override 
        nonReentrant 
        whenNotPaused 
        onlyEventManager 
    {
        Payment storage payment = payments[eventId][ticketId];
        require(payment.status == PaymentStatus.Pending, "Invalid payment status");
        require(!payment.isCancelled, "Ticket cancelled");
        require(!eventCancelled[eventId], "Event cancelled");
        
        payment.status = PaymentStatus.Released;
        emit PaymentReleased(eventId, payment.payer, payment.amount);
    }

    function refundPayment(uint256 eventId, uint256 ticketId) 
        external 
        override 
        nonReentrant 
        whenNotPaused 
    {
        Payment storage payment = payments[eventId][ticketId];
        require(payment.status == PaymentStatus.Pending, "Invalid payment status");
        require(payment.payer == msg.sender, "Not the payer");
        require(block.timestamp <= payment.refundDeadline, "Refund window closed");
        
        uint256 refundAmount = payment.amount;
        payment.status = PaymentStatus.Refunded;
        
        (bool success, ) = payable(msg.sender).call{value: refundAmount}("");
        require(success, "Refund transfer failed");
        
        emit PaymentRefunded(eventId, msg.sender, refundAmount);
    }

    function enableWaitlistRefund(uint256 eventId, uint256 ticketId) 
        external 
        override 
        whenNotPaused 
    {
        Payment storage payment = payments[eventId][ticketId];
        require(payment.status == PaymentStatus.Pending, "Invalid payment status");
        require(payment.payer == msg.sender, "Not the payer");
        require(!payment.isCancelled, "Ticket cancelled");
        
        payment.waitlistRefundEnabled = true;
        emit WaitlistRefundEnabled(eventId, ticketId);
    }

    function getPaymentDetails(uint256 eventId, uint256 ticketId) 
        external 
        view 
        returns (
            address payer,
            uint256 amount,
            PaymentStatus status,
            bool waitlistRefundEnabled,
            uint256 refundDeadline,
            bool isCancelled
        ) 
    {
        Payment memory payment = payments[eventId][ticketId];
        return (
            payment.payer,
            payment.amount,
            payment.status,
            payment.waitlistRefundEnabled,
            payment.refundDeadline,
            payment.isCancelled
        );
    }

    function isEventCancelled(uint256 eventId) external view returns (bool) {
        return eventCancelled[eventId];
    }

    function getPaymentStatus(uint256 eventId, uint256 ticketId) 
        external 
        view 
        override 
        returns (PaymentStatus) 
    {
        return payments[eventId][ticketId].status;
    }

    function getPaymentAmount(uint256 eventId, uint256 ticketId) 
        external 
        view 
        override 
        returns (uint256) 
    {
        return payments[eventId][ticketId].amount;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function withdrawStuckFunds() external onlyOwner {
        (bool success, ) = payable(owner()).call{value: address(this).balance}("");
        require(success, "Transfer failed");
    }
}

contracts/core/ResaleManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "../interfaces/ITicketFactory.sol";

contract ResaleManager is Ownable, ReentrancyGuard, Pausable {
    ITicketFactory public ticketFactory;

    struct ResaleListing {
        address seller;
        uint256 price;
        uint256 listingTime;
        bool isActive;
    }

    mapping(uint256 => ResaleListing) public resaleListings;

    uint256 public constant PLATFORM_FEE_PERCENTAGE = 5;
    uint256 public constant MAX_RESALE_MARKUP = 110; // 110% of original price
    uint256 public constant RESALE_TIMEOUT = 7 days;

    event TicketListed(uint256 indexed tokenId, address indexed seller, uint256 price);
    event TicketUnlisted(uint256 indexed tokenId, address indexed seller);
    event TicketResold(uint256 indexed tokenId, address indexed seller, address indexed buyer, uint256 price);
    event PlatformFeeCollected(uint256 indexed tokenId, uint256 amount);

    constructor(address ticketFactoryAddress) Ownable(msg.sender) {
        require(ticketFactoryAddress != address(0), "Invalid TicketFactory address");
        ticketFactory = ITicketFactory(ticketFactoryAddress);
    }

    function listTicketForResale(uint256 tokenId, uint256 price) external whenNotPaused {
        require(ticketFactory.ownerOf(tokenId) == msg.sender, "Not ticket owner");

        // Properly destructure the return values
        (, uint256 originalPrice, bool used, , , , ) = ticketFactory.getTicketDetails(tokenId);

        require(!used, "Ticket already used");
        uint256 maxResalePrice = (originalPrice * MAX_RESALE_MARKUP) / 100;
        require(price <= maxResalePrice, "Price exceeds max markup");

        resaleListings[tokenId] = ResaleListing({
            seller: msg.sender,
            price: price,
            listingTime: block.timestamp,
            isActive: true
        });

        emit TicketListed(tokenId, msg.sender, price);
    }

    function cancelResaleListing(uint256 tokenId) external {
        ResaleListing storage listing = resaleListings[tokenId];
        require(listing.seller == msg.sender, "Not seller");
        require(listing.isActive, "Listing is not active");

        listing.isActive = false;
        emit TicketUnlisted(tokenId, msg.sender);
    }

    function purchaseResaleTicket(uint256 tokenId) external payable nonReentrant whenNotPaused {
        ResaleListing storage listing = resaleListings[tokenId];
        require(listing.isActive, "Listing is not active");
        require(block.timestamp <= listing.listingTime + RESALE_TIMEOUT, "Listing expired");
        require(msg.value >= listing.price, "Insufficient payment");

        address seller = listing.seller;
        uint256 platformFee = (listing.price * PLATFORM_FEE_PERCENTAGE) / 100;
        uint256 sellerPayment = listing.price - platformFee;

        // Refund excess payment
        if (msg.value > listing.price) {
            (bool refundSuccess, ) = payable(msg.sender).call{value: msg.value - listing.price}("");
            require(refundSuccess, "Refund failed");
        }

        // Transfer funds
        (bool sellerSuccess, ) = payable(seller).call{value: sellerPayment}("");
        require(sellerSuccess, "Seller payment failed");

        (bool platformFeeSuccess, ) = payable(owner()).call{value: platformFee}("");
        require(platformFeeSuccess, "Platform fee transfer failed");

        // Transfer ticket ownership
        ticketFactory.transferFrom(seller, msg.sender, tokenId);

        // Mark listing as inactive
        listing.isActive = false;

        emit TicketResold(tokenId, seller, msg.sender, listing.price);
        emit PlatformFeeCollected(tokenId, platformFee);
    }

    function getResaleListing(uint256 tokenId)
        external
        view
        returns (
            address seller,
            uint256 price,
            uint256 listingTime,
            bool isActive
        )
    {
        ResaleListing memory listing = resaleListings[tokenId];
        return (listing.seller, listing.price, listing.listingTime, listing.isActive);
    }

    function isListingValid(uint256 tokenId) public view returns (bool) {
        ResaleListing memory listing = resaleListings[tokenId];
        return listing.isActive && block.timestamp <= listing.listingTime + RESALE_TIMEOUT;
    }

    function setTicketFactory(address newTicketFactory) external onlyOwner {
        require(newTicketFactory != address(0), "Invalid address");
        ticketFactory = ITicketFactory(newTicketFactory);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    receive() external payable {
        revert("Direct payments not accepted");
    }
}

contracts/core/TicketFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

interface IWaitlistManager {
    function getNextWaitingUser(uint256 eventId, uint256 zoneId) external view returns (address);
    function isUserWaiting(uint256 eventId, uint256 zoneId, address user) external view returns (bool);
    function getWaitlistLength(uint256 eventId, uint256 zoneId) external view returns (uint256);
}

contract TicketFactory is ERC721, Ownable, ReentrancyGuard {
    uint256 private _tokenIds;
    IWaitlistManager public waitlistManager;
    uint256 public constant PLATFORM_FEE_PERCENTAGE = 5;
    uint256 public constant MAX_RESALE_MARKUP = 110; // 110% of original price

    struct Event {
        uint256 maxSupply;
        uint256 currentSupply;
        uint256 price;
        bool isActive;
    }

    struct Ticket {
        uint256 eventId;
        uint256 price;
        bool used;
        uint256 seatNumber;
        bool isWaitlisted;
        bool isResale;
        uint256 resalePrice;
    }

    mapping(uint256 => Event) public events;
    mapping(uint256 => Ticket) public tickets;
    
    event EventCreated(uint256 indexed eventId, uint256 maxSupply, uint256 price);
    event TicketMinted(uint256 indexed tokenId, uint256 indexed eventId, uint256 price);
    event TicketUsed(uint256 indexed tokenId);
    event WaitlistTicketIssued(uint256 indexed tokenId, uint256 indexed eventId, address indexed user);
    event TicketListedForResale(uint256 indexed tokenId, uint256 price);
    event TicketPurchased(uint256 indexed tokenId, address indexed buyer, uint256 price);
    event TicketResold(uint256 indexed tokenId, address indexed buyer, uint256 price);

    constructor(address waitlistManagerAddress) ERC721("Event Ticket", "TCKT") Ownable(msg.sender) {
        require(waitlistManagerAddress != address(0), "Invalid waitlist manager address");
        waitlistManager = IWaitlistManager(waitlistManagerAddress);
    }

    function setWaitlistManager(address newWaitlistManager) external onlyOwner {
        require(newWaitlistManager != address(0), "Invalid address");
        waitlistManager = IWaitlistManager(newWaitlistManager);
    }

    function createEvent(
        uint256 eventId,
        uint256 maxSupply,
        uint256 price
    ) external onlyOwner {
        require(!events[eventId].isActive, "Event already exists");
        require(maxSupply > 0, "Invalid supply");
        require(price > 0, "Invalid price");

        events[eventId] = Event({
            maxSupply: maxSupply,
            currentSupply: 0,
            price: price,
            isActive: true
        });

        emit EventCreated(eventId, maxSupply, price);
    }

    function issueTicket(
        address to,
        uint256 eventId,
        uint256 seatNumber
    ) external onlyOwner returns (uint256) {
        Event storage event_ = events[eventId];
        require(event_.isActive, "Event not active");
        require(event_.currentSupply < event_.maxSupply, "Event sold out");

        _tokenIds++;
        uint256 newTokenId = _tokenIds;

        _mint(to, newTokenId);
        tickets[newTokenId] = Ticket({
            eventId: eventId,
            price: event_.price,
            used: false,
            seatNumber: seatNumber,
            isWaitlisted: waitlistManager.isUserWaiting(eventId, 0, to),
            isResale: false,
            resalePrice: 0
        });

        event_.currentSupply++;

        if (waitlistManager.isUserWaiting(eventId, 0, to)) {
            emit WaitlistTicketIssued(newTokenId, eventId, to);
        }

        emit TicketMinted(newTokenId, eventId, event_.price);
        return newTokenId;
    }

    function purchaseTicket(uint256 eventId, uint256 seatNumber) 
        external 
        payable 
        nonReentrant 
        returns (uint256) 
    {
        Event storage event_ = events[eventId];
        require(event_.isActive, "Event not active");
        require(event_.currentSupply < event_.maxSupply, "Event sold out");
        require(msg.value >= event_.price, "Insufficient payment");

        uint256 platformFee = (event_.price * PLATFORM_FEE_PERCENTAGE) / 100;
        uint256 refundAmount = msg.value - event_.price;

        (bool platformSuccess, ) = payable(owner()).call{value: platformFee}("");
        require(platformSuccess, "Platform fee transfer failed");

        if (refundAmount > 0) {
            (bool refundSuccess, ) = payable(msg.sender).call{value: refundAmount}("");
            require(refundSuccess, "Refund transfer failed");
        }

        _tokenIds++;
        uint256 newTokenId = _tokenIds;

        _mint(msg.sender, newTokenId);
        tickets[newTokenId] = Ticket({
            eventId: eventId,
            price: event_.price,
            used: false,
            seatNumber: seatNumber,
            isWaitlisted: false,
            isResale: false,
            resalePrice: 0
        });

        event_.currentSupply++;
        emit TicketPurchased(newTokenId, msg.sender, event_.price);
        emit TicketMinted(newTokenId, eventId, event_.price);
        return newTokenId;
    }

    function listForResale(uint256 tokenId, uint256 resalePrice) external {
        require(ownerOf(tokenId) == msg.sender, "Not owner");
        require(!tickets[tokenId].used, "Ticket used");
        uint256 maxAllowedPrice = (tickets[tokenId].price * MAX_RESALE_MARKUP) / 100;
        require(resalePrice <= maxAllowedPrice, "Price too high");

        tickets[tokenId].isResale = true;
        tickets[tokenId].resalePrice = resalePrice;
        emit TicketListedForResale(tokenId, resalePrice);
    }

    function purchaseResaleTicket(uint256 tokenId) 
        external 
        payable 
        nonReentrant 
    {
        Ticket storage ticket = tickets[tokenId];
        require(ticket.isResale, "Not for resale");
        require(!ticket.used, "Ticket used");
        require(msg.value >= ticket.resalePrice, "Insufficient payment");

        address seller = ownerOf(tokenId);
        uint256 platformFee = (msg.value * PLATFORM_FEE_PERCENTAGE) / 100;
        uint256 sellerPayment = msg.value - platformFee;

        (bool platformSuccess, ) = payable(owner()).call{value: platformFee}("");
        require(platformSuccess, "Platform fee transfer failed");

        (bool sellerSuccess, ) = payable(seller).call{value: sellerPayment}("");
        require(sellerSuccess, "Seller transfer failed");

        _transfer(seller, msg.sender, tokenId);
        ticket.isResale = false;
        
        emit TicketResold(tokenId, msg.sender, ticket.resalePrice);
    }

    function getTicketDetails(uint256 tokenId) 
        public 
        view 
        returns (
            uint256 eventId,
            uint256 price,
            bool used,
            uint256 seatNumber,
            bool isWaitlisted,
            bool isResale,
            uint256 resalePrice
        ) 
    {
        Ticket memory ticket = tickets[tokenId];
        return (
            ticket.eventId,
            ticket.price,
            ticket.used,
            ticket.seatNumber,
            ticket.isWaitlisted,
            ticket.isResale,
            ticket.resalePrice
        );
    }

    function getWaitlistCount(uint256 eventId) public view returns (uint256) {
        return waitlistManager.getWaitlistLength(eventId, 0);
    }

    function useTicket(uint256 tokenId) public {
        require(ownerOf(tokenId) == msg.sender, "Not ticket owner");
        require(!tickets[tokenId].used, "Ticket already used");
        
        tickets[tokenId].used = true;
        emit TicketUsed(tokenId);
    }
}

